import React, { useState } from 'react';

import './login.css';
import axios from '../../services/axios';

const Login = () => {
  const [countErors, setCountErrors] = useState(0);
  const [values, setValues] = useState({
    password: '123',
    email: 'test',
  });

  const handleSubmit = async (event) => {
    event.preventDefault();
    await axios.post('users/signin', values)
      .then((response) => console.log(response))
      .catch(() => setCountErrors(countErors + 1));
  };

  const handleChange = (event) => {
    const { name, value } = event.target;

    setValues({ ...values, [name]: value });
  };

  return (
    <div className="wrapper">
      <form className="form" onSubmit={handleSubmit}>
        <input className="inputField" name="email" value={values.email} onChange={handleChange} />
        <input className="inputField" name="password" value={values.password} onChange={handleChange} />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
