import * as React from 'react';

import Paper from '@mui/material/Paper';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';

const ListUsers = ({ users, offices }) => {
  const findOffice = (officeId) => {
    const officeName = offices.find((office) => (office.id === officeId));

    return officeName ? officeName.title : '';
  };

  return (
    <div className="listUsers_wrapper">
      <Table aria-label="simple table" className="table">
        <TableHead>
          <TableRow>
            <TableCell>Name</TableCell>
            <TableCell>Last Name</TableCell>
            <TableCell>Age</TableCell>
            <TableCell>User Role</TableCell>
            <TableCell>Email Address</TableCell>
            <TableCell>Office</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {!!users.length && !!offices.length && users.map((row) => (
            <TableRow
              className={(row.roleId === 1 && 'adminRow') || (row.active === 0 && 'disableUserRow')}
              key={row.id}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >
              <TableCell className={(row.active === 0 && 'disableUserRow')}>{row.firstName}</TableCell>
              <TableCell>{row.lastName}</TableCell>
              <TableCell>{row.lastName}</TableCell>
              <TableCell>{row.roleId === 1 ? 'Administrator' : 'User'}</TableCell>
              <TableCell>{row.email}</TableCell>
              <TableCell>{findOffice(row.officeID)}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </div>
  );
};

export default ListUsers;
