import React, { useState } from 'react';

import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';

import axios from '../../services/axios';

import styles from './addUser.module.css';

const AddUser = () => {
  const [values, setValues] = useState({
    email: '1',
    password: '1',
    firstName: '1',
    lastName: '1',
    officeID: '1',
    birthdate: '2022-06-04',
  });

  const handleChange = (event) => {
    const { name, value } = event.target;

    setValues({ ...values, [name]: value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    await axios.post('/users/add', values);
  };

  return (
    <div className={styles.wrapper}>
      <p className={styles.title}>Add user</p>
      <form onSubmit={handleSubmit}>
        <TextField
          required
          className={styles.inputField}
          label="Email"
          name="email"
          value={values.email}
          onChange={handleChange}
        />
        <TextField
          required
          className={styles.inputField}
          label="Password"
          name="password"
          type="password"
          value={values.password}
          onChange={handleChange}
        />
        <TextField
          required
          className={styles.inputField}
          label="First name"
          name="firstName"
          value={values.firstName}
          onChange={handleChange}
        />
        <TextField
          required
          className={styles.inputField}
          label="Last name"
          name="lastName"
          value={values.lastName}
          onChange={handleChange}
        />
        <TextField
          required
          className={styles.inputField}
          label="Office"
          name="officeID"
          value={values.officeID}
          onChange={handleChange}
        />
        <TextField
          InputLabelProps={{
            shrink: true,
          }}
          className={styles.dateField}
          id="date"
          label="Birthday"
          mask="mm/dd/yyyy"
          name="birthdate"
          type="date"
          value={values.birthdate}
          onChange={handleChange}
        />
        <div className={styles.buttonGroup}>
          <Button className={styles.button} type="submit" variant="outlined">Save</Button>
          <Button className={styles.cancelButton} type="button" variant="outlined">Cancel</Button>
        </div>
      </form>
    </div>
  );
};

export default AddUser;
