import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';

import axios from '../../services/axios';

import SuccessModal from './SuccessModal';

import styles from './addUser.module.css';

const AddUser = () => {
  const navigate = useNavigate();
  const [openModal, setOpenModal] = React.useState(false);
  const [values, setValues] = useState({
    email: '',
    password: '',
    firstName: '',
    lastName: '',
    officeID: '',
    birthdate: '',
  });

  const handleChange = (event) => {
    const { name, value } = event.target;

    setValues({ ...values, [name]: value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    await axios.post('/users/add', values);
    setOpenModal(true);
  };

  return (
    <div className={styles.wrapper}>
      <p className={styles.title}>Add user</p>
      <form onSubmit={handleSubmit}>
        <TextField
          required
          className={styles.inputField}
          label="Email"
          name="email"
          value={values.email}
          onChange={handleChange}
        />
        <TextField
          required
          className={styles.inputField}
          label="Password"
          name="password"
          type="password"
          value={values.password}
          onChange={handleChange}
        />
        <TextField
          required
          className={styles.inputField}
          label="First name"
          name="firstName"
          value={values.firstName}
          onChange={handleChange}
        />
        <TextField
          required
          className={styles.inputField}
          label="Last name"
          name="lastName"
          value={values.lastName}
          onChange={handleChange}
        />
        <TextField
          required
          className={styles.inputField}
          label="Office"
          name="officeID"
          value={values.officeID}
          onChange={handleChange}
        />
        <TextField
          InputLabelProps={{
            shrink: true,
          }}
          className={styles.dateField}
          id="date"
          label="Birthday"
          mask="mm/dd/yyyy"
          name="birthdate"
          type="date"
          value={values.birthdate}
          onChange={handleChange}
        />
        <div className={styles.buttonGroup}>
          <Button className={styles.button} type="submit" variant="outlined">Save</Button>
          <Button className={styles.cancelButton} type="button" variant="outlined" onClick={() => navigate('/admin')}>Cancel</Button>
        </div>
      </form>
      {openModal && <SuccessModal open={openModal} setOpen={setOpenModal} />}
    </div>
  );
};

export default AddUser;
